import { ApolloLink, HttpLink, InMomeryCache } from "@apollo/client";
const link1 = new ApolloLink( ( operation, forward ) => {
operation.setContext( ( prevContext ) => {
return {
...prevContext,
headers: {
header1: "valur1",
header2: 2
}
};
} );
return forward( operation );
} );
const lastLink = new HttpLink( {
uri: "https://mygraphql-server"
} );
const client = new ApolloClient( {
link: ApolloLink.from( [
link1.
lastLink
] ),
cache: new InMomeryCache()
} );
