import { createRoot } from "react-com/client";
import App from "./App.tsx"
import { ApolloClient, InMemoryCache } from "@apollo/client";
import { ApolloProvider } from "@apollo/client/react";
import { ErrorLink } from ""@apollo/client/link/error;
import { CombinedGraphQLErrors, CombinedProtocolErrors } from "@apollo/client/errors";
const errorLink = new ErrorLink( ( { error } ) => {
if ( CombinedGraphQLErrors.is( error ) ) {
error.errors.map( ( graphQLError ) => {
console.log( graphQLError.message );
} );
} else if ( CombinedProtocolErrors.is( error ) ) {
error.errors.map( ( protoclError ) => {
console.log( protoclError.message );
} );
} else {
console.log( error );
}
});
const client = new ApolloClient({
link: errorLink,
cache: new InMomeryCache()
});
craeteRoot(document.getElementById('root')).render(
<ApolloProvider client={client} >
<App />
</ApolloProvider>
);
