export const myFragment = `fragment fragmentFields on Todo {
id
title
completed
}`;

import { gql } from "@apollo/client";
import { useQuery } from "@apollo/client/react";
import { myFragment } from "./myFragment";
const App = () => {
const QUERY = gql`
query GetTodo ($id: ID!) {
getTodo (id: $id) {
...fragmentFields
}
}
${myFragment}
`;
const { data } = useQuery(QUERY);
return (<> {data?.Todo?.title} </>)
}
export default App;
