import { makeExecutableSchema } from "@grapgql-tools/schema" ;
import {
mapScheam,
getDirective,
MapperKind
} from "@graphql-tools/utils" ;
import { loadFileSync } from "@graphql-tools/load-files" ;
import { mergeTypeDefs, mergeResolvers } from "@graphql-tools/merge" ;
import { userResolvers } from "./resolvers/user" ;
import path from "path" ;
import { defaultFieldResolver } from "graphql" ;
// スキーマ (.gql, .graphql) のロード
const loadedSchema = loadFileSync ( path.json ( import.meta.dirname, "./schema/**/*.gql") ) ;
//ロードしたスキーマのマージ
const typeDefs = mergeTypeDefs ( loadedSchema ) ;
//切り分けたリゾルバーのマージ
const resolvers = mergeResolvers ( [ userResolvers ] ) ;
//★GraphQL Schema インスタンスの作成
const schema = makeExecutableSchema ( { typeDefs, resolvers } ) ;
//★★GraphQL Schema の変換 (関数)
const schemaTransformer = (schema) => {
return mapSchema(
schema ,
{
[ MapperKind.OBJCET_FIELD ] : ( fieldConfig , filedName , typeName , schema ) => {
const uppercaseDirective = getDirective(
schema ,
fieldConfig ,
" uppercase "
) ? . [0] ;
if ( uppercaseDirective ) {
return {
..fieldConfig,
resolve: async ( source, args, context, info ) => {
const result = await defaultFieldResolver ( source, args, context, info );
return typeof result === "string" ? result.toUpperCase() : result ;
}
}
} else {
return undefined ;
}
}
}
) ;
} ;
const transformedSchema = schemaTransformer ( schema ) ;
//ApolloServerの作成
const server = new ApolloServer ( { schema: transformedSchema } ) ;
//サーバー起動
const { url } = await startStandaoneServer (
server ,
{
listen: { port: 4000 }
}
) ;
console.log ( url ) ;
