import { makeExecutableSchema } from "@grapgql-tools/schema" ;
import {
mapScheam,
getDirective,
MapperKind
} from "@graphql-tools/utils" ;
import { loadFileSync } from "@graphql-tools/load-files" ;
import { mergeTypeDefs, mergeResolvers } from "@graphql-tools/merge" ;
import { userResolvers } from "./resolvers/user" ;
import path from "path" ;
import { defaultFieldResolver, graphQLError } from "graphql" ;
// スキーマ (.gql, .graphql) のロード
const loadedSchema = loadFileSync ( path.json ( import.meta.dirname, "./schema/**/*.gql") ) ;
//ロードしたスキーマのマージ
const typeDefs = mergeTypeDefs ( loadedSchema ) ;
//切り分けたリゾルバーのマージ
const resolvers = mergeResolvers ( [ userResolvers ] ) ;
//★GraphQL Schema インスタンスの作成
const schema = makeExecutableSchema ( { typeDefs, resolvers } ) ;
//★★GraphQL Schema の変換 (関数)
const schemaTransformer = (schema) => {
return mapSchema(
schema ,
{
[ MapperKind.OBJCET_FIELD ] : ( fieldConfig , filedName , typeName , schema ) => {
const authDirective = getDirective(
schema ,
fieldConfig ,
" auth "
) ? . [0] ;
if ( authDirective ) {
//ディレクティブがある場合contextの権限を確認しフィールドを返すかエラーを投げるか判断
//元々のリゾルバーを退避
const { resolve: originalResolve } = fieldConfig ;
//★resolverの上書き
fieldConfig.resolve = ( source, args, context, info ) => {
//contextから権限取得
const role = context.role ;
//権限がADMINではない場合エラー
if ( !role || role !== "ADMIN" ) {
throw new graphQLError( "有効な権限でのアクセスではありません" ) ;
}
return originalResolve ( source, args, context, info ) ;
}
return fieldConfig ; //カスタマイズしたresolverを返す
} else {
//ディレクティブがない場合はフィールドはカスタマイズしない
return undefined ;
}
}
}
) ;
} ;
const transformedSchema = schemaTransformer ( schema ) ;
//ApolloServerの作成
const server = new ApolloServer ( { schema: transformedSchema } ) ;
//サーバー起動
const { url } = await startStandaoneServer (
server ,
{
context: async ( { req } ) => {
const role = req.headers.authorization ?? "";
return {
role
};
},
listen: { port: 4000 }
}
) ;
console.log ( url ) ;
