import { ApolloServer } from "@apollo/server";
import { startStandaloneServer } from "@apollo/server/standalone";
//スキーマ定義const typeDefs = `#graphql
type Book {
id: ID!
title: String
}
type Query {
getBookByID (id: ID!): Book
}
type Mutation {
createBook(id: ID!, title: String)
}
`;
//DBデータの代わり
const books = [
{ //Book1
id: "1",
title: "グレートギャッツビー"
},
{ //Book2
id: "2",
title: "動物農場"
}
];
//リゾルバ定義
const resolvers = {
Query: {
getBookByID: (parent, args) => {
console.log("↓getBookByIDリゾルバに渡ってくるargs");
console.log(args);
return books;
}
},
Mutation: {
createBook: (parent, args) => {
console.log("↓craeteBookリゾルバに渡ってくるargs");
console.log(args);
//Bookの新規作成&登録処理
const newBook = {
id: args.id,
title: args.title
};
books.push(newBook)
return newBook;
}
}
};
//GraphQLサーバー作成
const server = new ApolloServer({
typeDefs,
resolvers
});
//htttpサーバーを起動(GraphQLサーバーをミドルウェアみたいに渡す)
const { url } = await startStandaloneServer(
server,
{
listen: { port: 4000 }
}
);
console.log(url);

