import { ApolloServer } from "@apollo/server";
import { startStandaloneServer } from "@apollo/server/standalone";
//↓自作の認証関数
import { isValidUser } from "./utils/Authentication"
//スキーマ定義const typeDefs = `#graphql
type Book {
id: ID!
title: String
}
type Query {
getBookByID (id: ID!): Book
}
type Mutation {
createBook(id: ID!, title: String)
}
`;
//DBデータの代わり
const books = [
{ //Book1
id: "1",
title: "グレートギャッツビー"
},
{ //Book2
id: "2",
title: "動物農場"
}
];
//リゾルバ定義
const resolvers = {
Query: {
getBookByID: (parent, args, context) => {
console.log("↓getBookByIDリゾルバに渡ってくるcontext");
console.log(context);
//認証チェック
if (!isValidUser(context.userName, context.password)) {
throw new Error("有効なユーザー名/パスワードではありません");
}
return books.find((book) => { return book.id === args.id });
}
},
Mutation: {
createBook: (parent, args, context) => {
console.log("↓createBookリゾルバに渡ってくるcontext");
console.log(context);
//認証チェック
if (!isValidUser(context.userName, context.password)) {
throw new Error("有効なユーザー名/パスワードではありません");
}
//Bookの新規作成&登録処理
const newBook = {
id: args.id,
title: args.title
};
books.push(newBook)
return newBook;
}
},
};
//contextの型定義
interface MyContext {
userName: string;
password: string;
}
//GraphQLサーバー作成
const server = new ApolloServer<MyContext>({
typeDefs,
resolvers
});
//htttpサーバーを起動(GraphQLサーバーをミドルウェアみたいに渡す)
const { url } = await startStandaloneServer(
server,
{
listen: { port: 4000 },
//↓contextの設定
context: ( { req } ) => {
return {
userName: req.headers['username'],
password: req.headers['password']
};
}
}
);
console.log(url);

query GetBookByID {
