import { ApolloServer } from "@apollo/server";
import { startStandaloneServer } from "@apollo/server/standalone";
//スキーマ定義const typeDefs = `#graphql
type Book {
id: ID!
title: String
}
type Query {
getBooks: [Book]
}
`;
//DBデータの代わり
const books = [
{ //Book1
id: "1",
title: "グレートギャッツビー"
},
{ //Book2
id: "2",
title: "動物農場"
}
];
//リゾルバ定義
const resolvers = {
Query: {
getBooks: (parent) => {
console.log("↓getBookByIDリゾルバに渡ってくるparent")
console.log(parent);
return books;
}
},
Book: {
id: (parent) => {
console.log("↓Bookのidリゾルバに渡ってくるparent")
console.log(parent)
return parent.id;
},
title: (parent) => {
console.log("↓Bookのtitleリゾルバに渡ってくるparent")
console.log(parent)
return parent.title
}
}
};
//GraphQLサーバー作成
const server = new ApolloServer({
typeDefs,
resolvers
});
//htttpサーバーを起動(GraphQLサーバーをミドルウェアみたいに渡す)
const { url } = await startStandaloneServer(
server,
{
listen: { port: 4000 }
}
);
console.log(url);
