import { ApolloServer } from "@apollo/server";
import { startStandaloneServer } from "@apollo/server/standalone";
//↓DBデータの代わり
import { libraries } from "./Library"
//スキーマ定義const typeDefs = `#graphql
type Library{
branch: String!
books: [Book]
}
type Book {
title: String!
author: Author!
}
type Author {
name: String!
}
type Query {
getAllLibrares: [Library]
getLibraryByBranch (branch: String!): Library
}
`;
//リゾルバ定義
const resolvers = {
Query: {
getAllLibrares: () => {
return libraries;
},
getLibraryByBranch: (_, args) => {
const branch = args.branch;
return libraries.find((library) => { return library.branch === branch});
}
},
};
//GraphQLサーバー作成
const server = new ApolloServer({
typeDefs,
resolvers
});
//htttpサーバーを起動(GraphQLサーバーをミドルウェアみたいに渡す)
const { url } = await startStandaloneServer(
server,
{
listen: { port: 4000 }
}
);
console.log(url);

